    private void btnVisualizarPessoaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVisualizarPessoaActionPerformed
        if (pessoa != null) {
            txtNomePessoa2.setText(pessoa.getNome());
            txtCpfPessoa2.setText(pessoa.getCpf());
            txtIdadePessoa2.setText(Integer.toString(pessoa.getIdade()));
            if (String.valueOf(cbxSexoPessoa1.getSelectedItem()).charAt(0) == 'M') {
                cbxSexoPessoa2.setSelectedItem("Masculino");
            } else {
                cbxSexoPessoa2.setSelectedItem("Feminino");
            }
        } else {
            JOptionPane.showMessageDialog(rootPane,
                    "Não há nenhuma pessoa cadastrada!");
        }
    }//GEN-LAST:event_btnVisualizarPessoaActionPerformed

