    private void cbxTipoVinculo1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_cbxTipoVinculo1ActionPerformed
        if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'H') {
            txtSalario1.setEditable(false);
            txtQtdHora1.setEditable(true);
            txtValorHora1.setEditable(true);
        } else if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'N') {
            txtQtdHora1.setEditable(false);
            txtValorHora1.setEditable(false);
            txtSalario1.setEditable(true);
        }
    }//GEN-LAST:event_cbxTipoVinculo1ActionPerformed

