    private void btnVisualizarAlunoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVisualizarAlunoActionPerformed
        if (aluno != null) {
            txtRa2.setText(aluno.getRa());
            txtNomeAluno2.setText(aluno.getNome());
            txtAc12.setText(Float.toString(aluno.getAc1()));
            txtAc22.setText(Float.toString(aluno.getAc2()));
            txtAg2.setText(Float.toString(aluno.getAg()));
            txtAf2.setText(Float.toString(aluno.getAf()));
            txtSituacao1.setText(aluno.verificarAprovacao());
        } else {
            JOptionPane.showMessageDialog(rootPane,
                    "Não há nenhum aluno cadastrado!");
        }
    }//GEN-LAST:event_btnVisualizarAlunoActionPerformed

    private void btnCadastrarFuncionarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarFuncionarioActionPerformed
        if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'H') {
            funcionario = new Funcionario(Integer.parseInt(txtCracha1.getText()),
                    txtNomeFuncionario1.getText(),
                    String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0),
                    Float.parseFloat(txtValorHora1.getText()),
                    Float.parseFloat(txtQtdHora1.getText()),
                    Float.parseFloat(txtDesconto1.getText()));

        } else if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'N') {
            funcionario = new Funcionario(Integer.parseInt(txtCracha1.getText()),
                    txtNomeFuncionario1.getText(),
                    String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0),
                    Float.parseFloat(txtSalario1.getText()),
                    Float.parseFloat(txtDesconto1.getText()));
        }
        JOptionPane.showMessageDialog(rootPane,
                "Funcionário cadastrado com sucesso!");
        limparCadastroFuncionario();
    }//GEN-LAST:event_btnCadastrarFuncionarioActionPerformed

    private void btnEditarRemuneracaoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditarRemuneracaoActionPerformed
        if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'H') {
            funcionario.setValorHora(Float.parseFloat(txtValorHora1.getText()));
            funcionario.setQtdHora(Float.parseFloat(txtQtdHora1.getText()));
        } else if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'N') {
            funcionario.setSalario(Float.parseFloat(txtSalario1.getText()));

        }
        JOptionPane.showMessageDialog(rootPane,
                "Remuneração editada com sucesso!");
        limparCadastroFuncionario();
    }//GEN-LAST:event_btnEditarRemuneracaoActionPerformed

    private void btnVisualizarFuncionarioActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVisualizarFuncionarioActionPerformed
        if (funcionario != null) {
            if (String.valueOf(cbxTipoVinculo1.getSelectedItem()).charAt(0) == 'H') {
                funcionario.setSalario(funcionario.calcularSalario());
            }
            txtNomeFuncionario2.setText(funcionario.getNome());
            txtCracha2.setText(Integer.toString(funcionario.getCracha()));
            txtDesconto2.setText(Float.toString(funcionario.getValorDesconto()));
            txtSalario2.setText(Float.toString(funcionario.getSalario()));
            txtValorReceber1.setText(Float.toString(funcionario.calcularValorReceber()));
        } else {
            JOptionPane.showMessageDialog(rootPane,
                    "Não há nenhum fucionário cadastrado!");
        }
    }//GEN-LAST:event_btnVisualizarFuncionarioActionPerformed

