    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        int codigo = Integer.parseInt(txtCodigo.getText());
        String nome = txtNome.getText();
        double percentualComissao = Double.parseDouble(txtPercentualComissao.getText());

        vendedor = new Vendedor(codigo, nome, percentualComissao);
        lstVendedor.add(vendedor);
        JOptionPane.showMessageDialog(rootPane,
                "Cadastrado com sucesso!");
        limpar();
    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void btnMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnMostrarActionPerformed
        String vendedores = "";

        for (Vendedor v : lstVendedor) {
            vendedores += "\n------------\n"
                    + v.imprimir();
        }

        JOptionPane.showMessageDialog(rootPane, vendedores);
    }//GEN-LAST:event_btnMostrarActionPerformed

