    private void btnSelecionarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSelecionarActionPerformed
        for (Vendedor v : lstVendedor) {
            if (v.getCodigo() == Integer.parseInt(txtPesquisar1.getText())) {
                txtNome1.setText(v.getNome());
            }
        }
        txtPesquisar1.setText("");
    }//GEN-LAST:event_btnSelecionarActionPerformed

    private void btnVenderActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnVenderActionPerformed
        double valorVenda = Double.parseDouble(txtValorVenda.getText());

        for (Vendedor v : lstVendedor) {
            if (v.getNome().equals(txtNome1.getText())) {
                if (!txtDesconto.getText().equals("")) {
                    double desconto = Double.parseDouble(txtDesconto.getText());

                    JOptionPane.showMessageDialog(rootPane,
                            v.imprimir(valorVenda, desconto));
                } else {
                    JOptionPane.showMessageDialog(rootPane,
                            v.imprimir(valorVenda));
                }
            }
        }
        txtValorVenda.setText("");
        txtDesconto.setText("");
    }//GEN-LAST:event_btnVenderActionPerformed

    public void limpar() {
        txtCodigo.setText("");
        txtNome.setText("");
        txtPercentualComissao.setText("");
    }

